create extension if not exists "uuid-ossp";

CREATE TABLE IF NOT EXISTS "user" (
    id   UUID unique    not null default uuid_generate_v1(),
    username VARCHAR(255) NOT NULL UNIQUE,
    firstname VARCHAR(255),
    lastname VARCHAR(255),
    image BYTEA
    );

